import React, { Component } from 'react'
import UserService from './UserService'

const userService = new UserService()

class UserList extends Component {

    constructor(props) {
        super(props)
        this.state = {
            users: [],
            nextPageURL: ''
        }
        // this.nextPage = this.nextPage.bind(this)
        this.handleDelete = this.handleDelete.bind(this)
    }


    componentDidMount () {
        let currentComponent = this;
        userService.getUsers().then(function (result) {
            let tmpArray = []
            for (var i = 0; i < result.length; i++) {
                tmpArray.push(result[i])
            }
            currentComponent.setState({
                users: tmpArray
            })
        });
    }

    /*handleCreate(firstName, lastName, contact_number, email, address){
        // const history = useHistory()
        userService.createUser(
              {              
                "first_name": firstName, // this.firstName.value,
                "last_name": lastName, // this.lastName.value,
                "address": address, // this.address.value,
                "contact_number": contact_number, // this.contact_number.value,
                "email": email, // this.email.value,
              }).then((result) => {
                  alert("Customer created")                
              }).catch(() =>{
                  alert("There was error! Please re-check your form.")
              })
        this.props.history.push("/")
      }*/

    handleDelete(event, pk){
        event.preventDefault()
        let currentComponent = this;

        userService.deleteUser({pk : pk}).then(() => {
            var newArr = currentComponent.state.users.filter(function(obj){
                return obj.pk !== pk;
            });
            currentComponent.setState({
                customers: newArr})
        });
    }


    render (){
        return (
            <div  className="users-list">
                <table  className="table">
                    <thead  key="thead">
                    <tr>
                        <th>#</th>
                        <th>First Name</th>
                        <th>Last Name</th>
                        <th>Phone</th>
                        <th>Email</th>
                        <th>Address</th>
                        <th>Actions</th>
                    </tr>
                    </thead>
                    <tbody>
                        {this.state.users.map( c  =>
                        <tr key={ c.id }>
                            <td>{c.id}</td>
                            <td>{c.first_name}</td>
                            <td>{c.last_name}</td>
                            <td>{c.contact_number}</td>
                            <td>{c.email}</td>
                            <td>{c.address}</td>
                            <td>
                            <button className="btn btn-sm btn-primary" onClick={(e)=>  this.handleDelete(e,c.id) }> Delete </button>
                            &nbsp;&nbsp;&nbsp;
                            <a className="btn btn-secondary btn-sm active" role="button" aria-pressed="true" href={"/users/" + c.id} > Update </a>
                            </td>
                        </tr>)}
                    </tbody>
                </table>
                <button  className="btn btn-primary"  onClick=  {  this.nextPage  }>Next</button>
            </div>
            );
    }
}

export default UserList;
