import React, { Component } from 'react'

import UserService from './UserService'

const userService = new UserService()


class UserCreate extends Component {

    constructor(props) {
        super(props)
        this.handleSubmit = this.handleSubmit.bind(this)
        this.firstName = React.createRef();
        this.lastName = React.createRef();
        this.address = React.createRef();
        this.contact_number = React.createRef();
        this.email = React.createRef();
    }

    componentDidMount(){
        const { match: { params } } = this.props;
        if(params && params.id)
        {
            userService.getUser(params.id).then((c)=>{
              this.firstName.value = c.first_name
              this.lastName.value = c.last_name
              this.address.value = c.address
              this.contact_number.value = c.contact_number
              this.email.value = c.email
              // this.refs.firstName.value = c.first_name;
            // this.refs.lastName.value = c.last_name;
            // this.refs.email.value = c.email;
            // this.refs.phone.value = c.phone;
            // this.refs.address.value = c.address;
            // this.refs.description.value = c.description;
          })
        }
    }

    handleCreate(){

      userService.createUser(
            {              
              "first_name": this.firstName.value,
              "last_name": this.lastName.value,
              "address": this.address.value,
              "contact_number": this.contact_number.value,
              "email": this.email.value,
            }).then((result) => {
                alert("Customer created")
            }).catch(() =>{
                alert("There was error! Please re-check your form.")
            })
    }

    handleSubmit(event) {
        const { match: { params } } = this.props
        if (params && params.id) {
            this.handleUpdate(params.id)
        }
        else{
            console.log(this.firstName, "-----------------------", this.firstName.value)
            this.handleCreate()
        }
        event.preventDefault()
    }

    render() {
        return (
          <form onSubmit={this.handleSubmit}>
          <div className="form-group">
            <label>
              First Name:</label>
              <input className="form-control" type="text" ref={this.firstName } value={this.firstName} />

            <label>
              Last Name:</label>
              <input className="form-control" type="text" ref={this.lastName} value={this.firstName} />

            <label>
              Phone:</label>
              <input className="form-control" type="text" ref={this.contact_number} value={this.firstName} />

            <label>
              Email:</label>
              <input className="form-control" type="text" ref={this.email} value={this.firstName} />

            <label>
              Address:</label>
              <input className="form-control" type="text" ref={this.address} value={this.firstName} />

            <input className="btn btn-primary" type="submit" value="Submit" />
            </div>
          </form>
        );
  }
}

export default UserCreate
