import React, { Component } from 'react'

import UserService from './UserService'

const userService = new UserService()

function MyFunctionComponent () {
    return <input className="form-control" type="text" />;
}


class UserCreate extends Component {

    constructor(props) {
        super(props)
        this.handleSubmit = this.handleSubmit.bind(this)
        this.firstName = React.createRef();
        this.address = React.createRef();
        this.contact_number = React.createRef();
        this.email = React.createRef();
    }

    componentDidMount(){
        const { match: { params } } = this.props;
        if(params && params.pk)
        {
            userService.getUser(params.pk).then((c)=>{
            this.refs.firstName.value = c.first_name;
            this.refs.lastName.value = c.last_name;
            this.refs.email.value = c.email;
            this.refs.phone.value = c.phone;
            this.refs.address.value = c.address;
            this.refs.description.value = c.description;
          })
        }
    }

    handleCreate(){
        userService.createUser(
            {
                "first_name": this.refs.firstName.value,
                "last_name": this.refs.lastName.value,
                "address": this.refs.address.value,
                "contact_number": this.refs.contact_number.value,
                "email": this.refs.email.value,
            }).then((result) => {
                alert("Customer created")
            }).catch(() =>{
                alert("There was error! Please re-check your form.")
            })
    }

    handleSubmit(event) {
        const { match: { params } } = this.props
        if (params && params.pk) {
            this.handleUpdate(params.pk)
        }
        else{
            console.log(this.props)
            this.handleCreate()
        }
        event.preventDefault()
    }

    render() {
        return (
          <form onSubmit={this.handleSubmit}>
          <div className="form-group">
            <label>
              First Name:</label>
              <MyFunctionComponent ref='{ this.firstName }' />

            <label>
              Last Name:</label>
              <input className="form-control" type="text" ref='lastName'/>

            <label>
              Phone:</label>
              <input className="form-control" type="text" ref='contact_number' />

            <label>
              Email:</label>
              <input className="form-control" type="text" ref='email' />

            <label>
              Address:</label>
              <input className="form-control" type="text" ref='address' />

            <input className="btn btn-primary" type="submit" value="Submit" />
            </div>
          </form>
        );
  }
}

export default UserCreate
